mapper.select:
select

from sys_admin
where  admin_login_name = #{adminLoginName,jdbcType=VARCHAR} or admin_mobile = #{adminLoginName,jdbcType=VARCHAR}

select

from sys_admin


		and admin_num = #{adminNum,jdbcType=VARCHAR}


		and admin_login_name = #{adminLoginName,jdbcType=VARCHAR}


		and admin_password = #{adminPassword,jdbcType=VARCHAR}


		and admin_mobile = #{adminMobile,jdbcType=VARCHAR}


		and state = #{state,jdbcType=INTEGER}


		and create_time = #{createTime,jdbcType=TIMESTAMP}


		and update_time = #{updateTime,jdbcType=TIMESTAMP}


		and last_opt_name = #{lastOptName,jdbcType=VARCHAR}


		and is_del = #{isDel,jdbcType=BIT}


		and is_frozen = #{isFrozen,jdbcType=BIT}


		and is_authentication = #{isAuthentication,jdbcType=BIT}



	limit ${pageIndex},${pageSize}

select
  count(1)
from sys_admin


		and admin_num = #{adminNum,jdbcType=VARCHAR}


		and admin_login_name = #{adminLoginName,jdbcType=VARCHAR}


		and admin_password = #{adminPassword,jdbcType=VARCHAR}


		and admin_mobile = #{adminMobile,jdbcType=VARCHAR}


		and state = #{state,jdbcType=INTEGER}


		and create_time = #{createTime,jdbcType=TIMESTAMP}


		and update_time = #{updateTime,jdbcType=TIMESTAMP}


		and last_opt_name = #{lastOptName,jdbcType=VARCHAR}


		and is_del = #{isDel,jdbcType=BIT}


		and is_frozen = #{isFrozen,jdbcType=BIT}


		and is_authentication = #{isAuthentication,jdbcType=BIT}

select 
	a.admin_mobile ,
	a.admin_num ,
	a.create_time,
	a.update_time ,
	d.`name` as adminName ,
	a.is_authentication,
	a.is_frozen
from 
				sys_admin a 
LEFT JOIN sys_admin_info d on a.admin_num = d.admin_num

LEFT JOIN sys_user_role b on a.admin_num = b.user_num
LEFT JOIN sys_role c on b.role_id = c.id



		AND a.admin_mobile = #{adminMobileSearch}


		AND d.`name` = #{adminNameSearch}


		AND a.create_time >= #{beginTimeSearch}


		AND a.create_time <= #{endTimeSearch}


			AND c.id  = #{roleNameSearch}



ORDER BY a.create_time DESC

		limit ${pageIndex},${pageSize}

select 
	count(1)
from 
				sys_admin a 
LEFT JOIN sys_admin_info d on a.admin_num = d.admin_num

LEFT JOIN sys_user_role b on a.admin_num = b.user_num
LEFT JOIN sys_role c on b.role_id = c.id



		AND a.admin_mobile = #{adminMobileSearch}


		AND d.`name` = #{adminNameSearch}


		AND a.create_time >= #{beginTimeSearch}


		AND a.create_time <= #{endTimeSearch}


		AND c.id  = #{roleNameSearch}
